import "./App.css";
import { useContext, useEffect, useState } from "react";
import { toggle } from "./useToggle"

function App(){
    const [isVisible, toggle] = useToggle()

    return (
        <div className="App">
            <button 
                onClick={toggle}
            >
                {isVisible ? "Hide" : "Show"}
            </button>
            {isVisible && <h1> Hidden text</h1>}
        </div>
    );
}

export default App;
