import "./App.css"
import Axios from "axios";
import { useEffect, useState } from "react";
import GoogleFonts from 'google-fonts';



function App() {
    const [ catFact, setCatFact ] = useState("");
    const [ catImg, setCatImg ] = useState("");

    const fetchCatFact = () => {
        Axios.get("https://catfact.ninja/fact").then((res) => {
            setCatFact(res.data.fact);
        });
    }

    const fetchCatImg = () => {
        const randomQuery = Math.random().toString(36).substring(7)
        const imgUrl = `https://cataas.com/cat?${randomQuery}`
        setCatImg(imgUrl)
        // Axios.get("https://cataas.com/cat").then((res) => {
        //     setCatImg(res.request.responseURL);
        // });
    }

    useEffect(() => {
        fetchCatFact();
        fetchCatImg();
    }, []);   

    return (
        <div className="App">
            <button 
                onClick={ () => {
                    fetchCatFact();
                    fetchCatImg();
            }}>Generate Cat Fact 
            </button>
            <p>{catFact}</p>
           { catImg && <img src={catImg}/>} 
        </div>
    )
}


export default App;
