import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [catFact, setCatFact] = useState('');
  const [catImg, setCatImg] = useState('');
  const [isLoading, setIsLoading] = useState(true);

  const fetchCatData = () => {
    setIsLoading(true); // Set loading state to true while fetching
    const factPromise = axios.get('https://catfact.ninja/fact');
    const imgPromise = axios.get('https://cataas.com/cat');

    Promise.all([factPromise, imgPromise])
      .then(([factResponse, imgResponse]) => {
        setCatFact(factResponse.data.fact);
        setCatImg(imgResponse.request.responseURL);
        setIsLoading(false); // Set loading state to false after fetching
      })
      .catch((error) => {
        console.log('Error fetching cat data:', error);
        setIsLoading(false); // Set loading state to false if there is an error
      });
  };

  useEffect(() => {
    fetchCatData();
  }, []);

  return (
    <div className="App">
      <button
        onClick={() => {
          fetchCatData();
        }}
      >
        Generate Cat Fact
      </button>
      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <>
          <p>{catFact}</p>
          {catImg && <img src={catImg} alt="Random Cat" />}
        </>
      )}
    </div>
  );
}

export default App;
