import "./App.css"
import Axios from "axios";
import { useEffect, useState } from "react";

function App() {

    const [ type, setType ] = useState("");
    const [ excuse, setExcuse ] = useState("");

    const fetchData = () => {
        Axios.get(`excuser.herokuapp.com/v1/excuse${type}`).then((res) => {
            setExcuse(res.data);
        })
    }


    return (
        <div className="App">
            <h1> Generate an excuse</h1>
            <button onClick={() => {
                setType("party")
            }}> Party </button>
            <button> Family </button>
            <button> Office </button>
            <p>Excuse: {excuse}</p>
        </div>
    )
}

export default App;
